                    {/* <div className="pod_data">
                    <div className="disp_data_label">Дата доставки</div>
                    <div className="disp_data_input"><input id="date" className="pod_input" type="date"></input></div>
                    <div className="disp_data_label">Время доставки</div>
                    <div className="disp_data_input"><input id="time" className="pod_input" type="time"></input></div>
                    <div className="disp_data_label">ФИО получателя</div>
                    <div className="disp_data_input"><input id="recient" className="pod_input" type="text"></input></div>
                    <div className="disp_data_label">Принятая сумма наличных</div>
                    <div className="disp_data_input"><input id="summ" className="pod_input" type="number"></input></div>
                    <div className="disp_data_label">Комментарий</div>
                    <div className="disp_data_input"><input id="comment" className="pod_input" type="text"></input></div>
                </div> */}
                    <button onClick={this.reciept.bind(this)} className="send_pod">Принять на склад и закрыть</button>
                </div>) : (null)}

                {this.props.store.login.disp_map ? (
                    <div className="search_box">
                        <input className="search_input" value={this.props.store.disp.search_box} onChange={(e)=>(this.props.set_disp_search_box(e.target.value))}></input>
                <button className="search_button" onClick={this.search.bind(this)}>Найти</button>
                </div>
                ):(null)}

                {this.props.store.login.disp_map ? (<div className='disp_map'>


              <GoogleMapReact
                    bootstrapURLKeys={{ key: 'AIzaSyD5AmmHNIXXN0yquTsPxoXuvtOp8OYhe2E' }}
                    defaultCenter={center}
                    defaultZoom={14}
                    yesIWantToUseGoogleMapApiInternals
                    onGoogleApiLoaded={({ map, maps }) => onGoogleApiLoaded(map, maps)}
                >

                </GoogleMapReact>

                </div>):(null)}
                {this.props.store.login.disp_map ? (<button className="search_box" onClick={this.save_lat_lng.bind(this)}>Сохранить</button>):(null)}
                {this.props.store.login.disp_map ? (<button className="search_box" onClick={this.sound_test.bind(this)}>Тест звука</button>):(null)}
                <Sound
      url={test_sound}
      playStatus={this.props.store.general.test_sound}
      playFromPosition={100 /* in milliseconds */}
      onLoading={console.log('1')}
      onPlaying={console.log('2')}
      onFinishedPlaying={console.log('3')}
    />
            </div>

        );
    }
};

