import QrReader from 'react-qr-reader';
import { connect } from 'react-redux';

class Test extends Component {
    constructor(props, context) {
        super(props, context);

        this.state = {
            result: 'No result'
        }
    }


    handleScan = data => {
        if (data) {
            this.setState({
                result: data
            })
        }
    }
    handleError = err => {
        console.error(err)
    }
    render() {
        return (
            <div>

                <p>{this.state.result}</p>
            </div>
        )
    }
}


