import React from 'react';
import { connect } from 'react-redux';
import './mobile.css';
import { get_data } from '../common/common_modules';
import './mobile_disp.css';
import './mobile_finance.css';

class Screen extends React.Component {

    active_window = (window) => {
        this.props.set_active_window(window);
    }

    update = (type) => {
        this.props.set_active_window("wait");

        get_data('cash').then(
            (result) => {
                console.log(result);
                // this.props.set_balance(result);

                // const list_data = { userkey: this.props.store.login.userkey };
                // get_data('list', list_data).then(
                //     (result) => {
                //         this.props.set_balance(result);
                //         console.log(result);
                //     },
                //     (err) => {
                //         console.log(err);
                //         alert("Ошибка!");
                //     }
                // );
            },
            (err) => {
                alert("Ошибка!!!");
                console.log(err)
            }
        );
    };

    render() {

        return (
            <div>
                <div className="mobile_heading">
                    Финансы
                </div>
                <div className="mobile_container">
                    <div className="finance_row">
                        <div className="finance_item">Баланс:</div>
                        <div className="finance_item">{this.props.store.movement.balance}</div>
                    </div>
                    <div className="finance_row">
                        <button className="mobile_finance_button" onClick={this.active_window.bind(this, 'm_movement')}>Движения</button>
                        <button className="mobile_finance_button" onClick={this.update.bind(this)}>Обновить</button>
                    </div>

                    <div className="finance_row">
                        <div className="finance_item">Начислено:</div>
                        <div className="finance_item">100.00</div>
                    </div>
                    <div className="finance_row">
                        <button className="mobile_finance_button" onClick={this.active_window.bind(this, 'm_bounty')}>Движения</button>
                        <button className="mobile_finance_button">Обновить</button>
                    </div>
                </div>
            </div>
        )
    }
};

export default connect(
    state => ({
        store: state
    }),
    dispatch => ({
        set_active_window: (param) => { dispatch({ type: 'set_active_window', payload: param }); },
        set_balance: (param) => { dispatch({ type: 'set_balance', payload: param }); },
    })

)(Screen);
