import React from 'react';
import { connect } from 'react-redux';
import './mobile.css';
import { get_data } from '../common/common_modules';
import './mobile_disp.css';
import './mobile_finance.css';

class Screen extends React.Component {

    update = (type) => {
        let from = this.props.store.movement.date_start.split("-").reverse().join("-").replace(/-/g, '') + "_" + this.props.store.movement.time_start.replace(':', '');
        let to = this.props.store.movement.date_end.replace(/-/g, '').split("").reverse().join("") + "_" + this.props.store.movement.time_end.replace(':', '');

        const data = {
            userkey: this.props.store.login.userkey,
            date_from: from,
            date_to: to,
        }
        console.log(data);
        get_data('cashlist', data).then(
            (result) => {
                console.log(result);
            },
            (err) => {
                alert("Ошибка!");
                console.log(err)
            }
        );
    };

    render() {

        return (
            <div>
                <div className="mobile_heading">
                    Движения ДС
                </div>
                <div className="mobile_container">
                    <div className="finance_row">
                        <input className="mobile_movememt_date" onChange={e => this.props.set_date_start(e.target.value)} type="date" />
                        {/* <input onChange={e => this.props.set_time_start(e.target.value)} className="mobile_del_input" type="time"></input> */}

                        <input className="mobile_movememt_date" onChange={e => this.props.set_date_end(e.target.value)} type="date" />
                        {/* <input onChange={e => this.props.set_time_end(e.target.value)} className="mobile_del_input" type="time"></input> */}
                    </div>
                    <button className="mobile_finance_button--b" onClick={this.update.bind(this)}>Получить данные</button>
                    <div className="finance_row">
                        <div className="finance_item">Остаток на начало</div>
                        <div className="finance_item">{this.props.store.movement.balance_start}</div>
                    </div>

                    <div className="mobile_disp_address_data">
                        <div className="disp_address_data_header disp_address_data_header">Список</div>
                        <div className="disp_address_data_el mobile_disp_address_data_el">

                            <div className="cargo_item">
                                Доставка накладной получателю<br/>
                                000370727<br />
                                25.02.2021 14:43:49 123123123123<br />
                            </div>

                        </div>
                    </div>

                    <div className="finance_row">
                        <div className="finance_item">Остаток на конец</div>
                        <div className="finance_item">{this.props.store.movement.balance_end}</div>
                    </div>
                </div>
            </div>
        )
    }
};

export default connect(
    state => ({
        store: state
    }),
    dispatch => ({
        set_active_window: (param) => { dispatch({ type: 'set_active_window', payload: param }); },
        set_date_start: (param) => { dispatch({ type: 'set_date_start', payload: param }) }, 
        set_time_start: (param) => { dispatch({ type: 'set_time_start', payload: param }) }, 
        set_date_end: (param) => { dispatch({ type: 'set_date_end', payload: param }) },
        set_time_end: (param) => { dispatch({ type: 'set_time_end', payload: param }) }, 
    })
)(Screen);
