const today = new Date()

let H = today.getHours();
let M = today.getMinutes();
let S = today.getSeconds();

const time = H + M + S;

const initialState = {
    date_start: '',
    time_start: time,
    date_end: '',
    time_end: time,

    balance: '',
    balance_start: '',
    balance_end: '',

    profit: '',
    profit_for_period: '',
}

export default function dispatch(state = initialState, action) {
    switch (action.type) {
        case 'set_date_start': return { ...state, date_start: action.payload }
        case 'set_date_end': return { ...state, date_end: action.payload }
        case 'set_balance': return { ...state, balance: action.payload }
        case 'set_profit': return { ...state, profit: action.payload }
        case 'set_time_start': return { ...state, time_start: action.payload }
        case 'set_time_end': return { ...state, time_end: action.payload }

        default: return state
    }
}
