                    {this.props.store.order.data.SummVAT === '' ? (null):(
                        <div className="disp_data_label">В том числе НДС:</div>
                    )}
                    {this.props.store.order.data.SummVAT === '' ? (null):(
                        <div className="disp_data_el">{this.props.store.order.data.SummVAT}</div>  
