import React from 'react';
import { connect } from 'react-redux';
import Select from 'react-select'
import { customStyles } from "./../../common/common_style";
import { Card, Input, Button } from 'semantic-ui-react'

const oop_list = [
    { label: "ООО", value: "ООО" },
    { label: "ПАО", value: "ПАО" },
    { label: "Другое", value: "Другое" }
]

class Screen extends React.Component {



    render() {

        return (
            <div>
                <div className="disp_Number">
                    Предоставление данных для заключения договора оказания транспортных услуг
                </div>
                <div className='contract_data'>


                        <Button onClick={() => this.props.set_org_type(true)} >Организация</Button>

                        <Button onClick={() => this.props.set_org_type(false)}>Индивидуальный предпрениматель</Button>


                    {/* <div className="disp_data_label">Организация</div>
                    <div>
                        <input
                            name="send_type"
                            type="radio"
