              get_data('dispatch', data).then(
                (result) => {

                  this.props.set_data_disp(result);
                  this.props.set_active_window("disp");
                  this.props.set_last_window("create_disp");

                },
                (err) => { console.log(err) }
              );
        },
        (err) => { console.log(err) }
      );
